import { validateAndGetMessage } from '../utils';
import { useChannelStateContext, useTranslationContext } from '../../../context';
export const useMarkUnreadHandler = (message, notifications = {}) => {
    const { getErrorNotification, getSuccessNotification, notify } = notifications;
    const { channel } = useChannelStateContext('useMarkUnreadHandler');
    const { t } = useTranslationContext('useMarkUnreadHandler');
    return async (event) => {
        event.preventDefault();
        if (!message?.id) {
            console.warn('Mark unread handler does not have access to message id');
            return;
        }
        try {
            await channel.markUnread({ message_id: message.id });
            if (!notify)
                return;
            const successMessage = getSuccessNotification &&
                validateAndGetMessage(getSuccessNotification, [message]);
            if (successMessage)
                notify(successMessage, 'success');
        }
        catch (e) {
            if (!notify)
                return;
            const errorMessage = getErrorNotification && validateAndGetMessage(getErrorNotification, [message]);
            if (getErrorNotification && !errorMessage)
                return;
            notify(errorMessage ||
                t('Error marking message unread. Cannot mark unread messages older than the newest 100 channel messages.'), 'error');
        }
    };
};
