import React, { useEffect, useState } from 'react';
import { StreamChat } from 'stream-chat';
import {
  Chat,
  Channel,
  ChannelHeader,
  MessageList,
  MessageInput,
} from 'stream-chat-react';
// ← change this:
import 'stream-chat-css/dist/css/index.css';

const API_KEY = '3nkn6wg7k7zt';
const USER_ID = 'daftuser';

export default function App() {
  const [client, setClient] = useState(null);

  useEffect(() => {
    (async () => {
      const res = await fetch('/token', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: USER_ID }),
      });
      if (!res.ok) throw new Error('Token fetch failed: ' + res.status);
      const { token } = await res.json();

      const c = StreamChat.getInstance(API_KEY);
      await c.connectUser({ id: USER_ID, name: 'Daft User' }, token);
      setClient(c);
    })();
  }, []);

  if (!client) return <div>Loading chat…</div>;

  const channel = client.channel('messaging', 'daft-channel', { name: 'Daft Chat' });

  return (
    <Chat client={client} theme="messaging light">
      <Channel channel={channel}>
        <ChannelHeader />
        <MessageList />
        <MessageInput />
      </Channel>
    </Chat>
  );
}
