    const { textComposer } = useMessageComposer();
    const containerRef = useRef(null);
    const handleSelect = useCallback(() => {
        textComposer.handleSelect(item);
    }, [item, textComposer]);
    useLayoutEffect(() => {
        if (!focused)
            return;
        containerRef.current?.scrollIntoView({ behavior: 'instant', block: 'nearest' });
