        }), onMouseEnter: onMouseEnter, ref: containerRef },
        React.createElement("button", { onClick: handleSelect, onKeyDown: (event) => {
                if (event.key === 'Enter') {
                    handleSelect();
                }
            }, ref: innerRef },
            React.createElement(Component, { entity: item, focused: focused }))));
